sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate) {
		var now = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		return testdate.before(now);
	},
	isDateToday: function(strDate) {
		var today = new GlideDateTime().getLocalDate();
		gs.info('today: ' + today);
		var testDay = new GlideDateTime(strDate).getLocalDate();
		gs.info('testDay: ' + testDay);

		gs.info('comp: ' + today.compareTo(testDay));

		return today.compareTo(testDay) === 0;
	},
    type: 'NeedItUtils'
};
