sysauto_script.script:
var now = new GlideDateTime();
var oneDayFromNow = new GlideDateTime();
oneDayFromNow.addDaysLocalTime(1);
gs.info('now ' + now.getDisplayValue());
gs.info('oneDayFromNow ' + oneDayFromNow.getDisplayValue());

var dueNITasks = new GlideRecord('x_58872_needit_needit_task');
dueNITasks.addQuery('due_date', '>=', now);
dueNITasks.addQuery('due_date', '<', oneDayFromNow);
dueNITasks.addQuery('state', '!=', 3);

dueNITasks.query();

while (dueNITasks.next()) {
    gs.info('Due NeedIt Task record = ' + dueNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', dueNITasks, dueNITasks.number, gs.getUserName());
}
